$('#pagination a').click(function() {
    $this = $(this);
    $this.addClass('load').text("加载中..."); //给a标签加载一个loading的class属性，可以用来添加一些加载效果
    var href = $this.attr("href"); //获取下一页的链接地址
    if (href != undefined) {
        //如果地址存在
        $.ajax({
            //发起ajax请求
            url: href, //请求的地址就是下一页的链接
            type: "get", //请求类型是get
            error: function(request) {
                //如果发生错误怎么处理
                console.log(request)
            },
            success: function(data) {
                //请求成功
                console.log(data)
                setTimeout(function() {
                    console.log(data)
                    $this.removeClass('load').text("查看更多"); //移除loading属性
                    var $res = $(data).find(".cont-list"); //从数据中挑出文章数据，请根据实际情况更改
                    $('.cont').append($res.fadeIn(500)); //将数据加载加进posts-loop的标签中。
                    var newhref = $(data).find("#pagination a").attr("href"); //找出新的下一页链接
                    if (newhref != undefined) {
                        $("#pagination a").attr("href", newhref);
                    } else {
                        $("#pagination").html(''); //如果没有下一页了，隐藏
                    }
                }, 500);
            }
        });
    }
    return false;
});
$('.nav li:eq(0)').addClass('active');
$('.index .cont:eq(0)').addClass('active');
$(function(){
	$('.nav li').click(function(){
		indexC = $(this).index();
		$(this).addClass('active').siblings().removeClass('active');
		$('.cont').eq(indexC).addClass('active').siblings().removeClass('active');
		})
    })
$(function() {
    $(".nav li").on("click", function() {
        var index = $(this).index();
        var qmkd = 0;
        for (var i = 0; i < index; i++) {
            qmkd += $('.nav li').eq(i).outerWidth(true);
        }
        $(".nav").animate({
            scrollLeft: qmkd - $(window).width()/2 + $(this).outerWidth()
        });
    });
});
$(document).ready(function(){  
    $(".change li a").each(function(){  
        $this = $(this);  
        if($this[0].href==String(window.location)){ 
            $(".change li").removeClass("on");
            $this.parent().addClass("on");   
        } 
    });  
});
